package DataStructures.Hackerrank.Medium;

public class MorganAndString {
    public static void main(String[] args) {
        String a = "";
        String b = "";
        System.out.println(MorganAndString.morganAndString(a, b));
    }

    static String[][] cache;
    static String morganAndString(String a, String b) {
        int m = a.length(), n = b.length(), size = m+n;
        char[] arr = new char[size];
        cache = new String[m+1][n+1];
        return helper(a, b, 0, 0, arr, 0);
    }

    static String helper(String a, String b, int aIdx, int bIdx, char[] result, int resIdx){
        if(aIdx == a.length() && bIdx == b.length()){
            return String.valueOf(result);
        }

        if(cache[aIdx][bIdx] != null) return cache[aIdx][bIdx];

        String ans = null;
        if(aIdx == a.length()){
            result[resIdx] = b.charAt(bIdx);
            ans = helper(a, b, aIdx, bIdx + 1, result, resIdx + 1);
        } else if(bIdx == b.length()){
            result[resIdx] = a.charAt(aIdx);
            ans = helper(a, b, aIdx + 1, bIdx, result, resIdx + 1);
        }
        else if(a.charAt(aIdx) == b.charAt(bIdx)){
            result[resIdx] = a.charAt(aIdx);
            String v1 = helper(a, b, aIdx + 1, bIdx, result, resIdx + 1);

            result[resIdx] = b.charAt(bIdx);
            String v2 = helper(a, b, aIdx, bIdx + 1, result, resIdx + 1);
            if(v1.compareTo(v2) > 0){
                ans = v2;
            } else{
                ans = v1;
            }
        } else if(a.charAt(aIdx) < b.charAt(bIdx)){
            result[resIdx] = a.charAt(aIdx);
            ans = helper(a, b, aIdx + 1, bIdx, result, resIdx + 1);
        } else{
            result[resIdx] = b.charAt(bIdx);
            ans = helper(a, b, aIdx, bIdx + 1, result, resIdx + 1);
        }
        cache[aIdx][bIdx] = ans;
        return ans;
    }
}
